                this.arrayaftersort = "Mảng không hợp lệ.";
                return;
            }
        });
        inputArray.disabled = true;
        btnSubmit.disabled = true;
        btnReset.disabled = true;
        const result = await this._HomeService.sendGetRequest(data).toPromise();
        if (result) {
            this.arrayaftersort = "Kết quả: " + result;
        }
        inputArray.disabled = false;
        btnSubmit.disabled = false;
        btnReset.disabled = false;
    }
    btnreset_onClick() {
        const inputArray = document.querySelector('#inputArray');
        inputArray.value = "";
        this.arrayaftersort = "";
    }
}
